# Find the thirteen adjacent digits in the 1000-digit number that have the greatest product. What is the value of this product?

import numpy as np


def main():

    highest = 0
    # Importing the numbers as strings
    input_string1 = ""
    input_string2 = ""

    # Loading the strings to the num_input array
    num_input = np.empty(1000)
    for x in range (0,len(input_string1)):
        num_input[x] = int(input_string1[x])
    for x in range (0,len(input_string2)):
        num_input[x+500] = int(x=input_string2[x])


    for x in range(0,len(num_input - 13)-13):
        temp = 1
        for y in range(x+0,x+13):
            temp *= num_input[y]
        if temp > highest:
            highest = temp

    print("The highest product is %d!" %(highest))

if __name__ == "__main__":
    main()
